namespace Pay\Controller;

use Think\Exception;

class AliscanController extends PayController
{
    public function __construct()
    {
        parent::__construct();
    }

    //支付
    public function Pay($array)
    {
        $orderid = I("request.pay_orderid");
        $body = I('request.pay_productname');
        $notifyurl = $this->_site . 'Pay_Aliscan_notifyurl.html'; //异步通知
        $callbackurl = $this->_site . 'Pay_Aliscan_callbackurl.html'; //返回通知

        $parameter = array(
            'code' => 'Aliscan', // 通道名称
            'title' => '支付宝官方扫码',
            'exchange' => 1, // 金额比例
            'gateway' => '',
            'orderid' => '',
            'out_trade_id' => $orderid,
            'body'=>$body,
            'channel'=>$array
        );

        // 订单号，可以为空，如果为空，由系统统一的生成
        $return = $this->orderadd($parameter);

       /* $this->EditMoney($return['orderid'], 'Aliscan', 0);
        die;*/
        $return['subject'] = $body;
        $paymethod = 'alipayPrecreate';
        switch ($paymethod)
        {
            case 'alipayPage':
                $this->alipayPage($return);
                break;
            case 'alipayWap':
                $this->alipayWap($return);
                break;
            case 'alipayPrecreate':
                $this->alipayPrecreate($return);
                break;
        }
    }

    protected function alipayPage($params)
    {
        $data = array(
            'out_trade_no'=>$params['orderid'],
            'total_amount'=>$params['amount'],
            'subject'=>$params['subject'],
            'product_code' => "FAST_INSTANT_TRADE_PAY"
        );
        $sysParams = json_encode($data,JSON_UNESCAPED_UNICODE);

        vendor('Alipay.aop.AopClient');
        vendor('Alipay.aop.SignData');
        vendor('Alipay.aop.request.AlipayTradePagePayRequest');
        $aop = new \AopClient();
        $aop->gatewayUrl = 'https://openapi.alipay.com/gateway.do';
        $aop->appId = $params['mch_id'];
        $aop->rsaPrivateKey = '';
        $aop->alipayrsaPublicKey= $params['signkey'];
        $aop->apiVersion = '1.0';
        $aop->signType = 'RSA2';
        $aop->postCharset='UTF-8';
        $aop->format='json';
        $aop->debugInfo=true;
        $request = new \AlipayTradePagePayRequest();
        $request->setBizContent($sysParams);
        $request->setNotifyUrl($params['notifyurl']);
        $request->setReturnUrl($params['callbackurl']);
        $result = $aop->pageExecute($request,'post');
        echo $result;
    }
    protected function alipayWap($params){

        $data = array(
            'out_trade_no'=>$params['orderid'],
            'total_amount'=>$params['amount'],
            'subject'=>$params['subject'],
            'product_code' => "FAST_INSTANT_TRADE_PAY"
        );
        $sysParams = json_encode($data,JSON_UNESCAPED_UNICODE);

        vendor('Alipay.aop.AopClient');
        vendor('Alipay.aop.SignData');
        vendor('Alipay.aop.request.AlipayTradeWapPayRequest');
        $aop = new \AopClient();
        $aop->gatewayUrl = 'https://openapi.alipay.com/gateway.do';
        $aop->appId = $params['mch_id'];
        $aop->rsaPrivateKey = '';
        $aop->alipayrsaPublicKey= $params['signkey'];
        $aop->apiVersion = '1.0';
        $aop->signType = 'RSA2';
        $aop->postCharset='UTF-8';
        $aop->format='json';
        $request = new \AlipayTradeWapPayRequest ();
        $request->setBizContent($sysParams);
        $request->setNotifyUrl($params['notifyurl']);
        $request->setReturnUrl($params['callbackurl']);
        $result = $aop->pageExecute ( $request,"post");
        echo $result;
    }

    protected function alipayPrecreate($params)
    {
        //组装系统参数
        $data = array(
            'out_trade_no'=>$params['orderid'],
            'total_amount'=>$params['amount'],
            'subject'=>$params['subject'],
        );
        $sysParams = json_encode($data,JSON_UNESCAPED_UNICODE);

        vendor('Alipay.aop.AopClient');
        vendor('Alipay.aop.SignData');
        vendor('Alipay.aop.request.AlipayTradePrecreateRequest');
        $aop = new \AopClient();
        $aop->gatewayUrl = 'https://openapi.alipay.com/gateway.do';
        $aop->appId = $params['mch_id'];
        $aop->rsaPrivateKey = '';
        $aop->alipayrsaPublicKey= $params['signkey'];
        $aop->apiVersion = '1.0';
        $aop->signType = 'RSA2';
        $aop->postCharset='UTF-8';
        $aop->format='json';
        $request = new \AlipayTradePrecreateRequest ();
        $request->setBizContent($sysParams);
        $request->setNotifyUrl($params['notifyurl']);
        $result = $aop->execute ( $request);
        $responseNode = str_replace(".", "_", $request->getApiMethodName()) . "_response";
        $resultCode = $result->$responseNode->code;

        if(!empty($resultCode)&&$resultCode == 10000){
            import("Vendor.phpqrcode.phpqrcode",'',".php");
            $url = urldecode($result->$responseNode->qr_code);
            $QR = "Uploads/codepay/". $params["orderid"] . ".png";//已经生成的原始二维码图
            $delqr = $QR;
            \QRcode::png($url, $QR, "L", 20);
            $this->assign("imgurl", $this->_site.$QR);
            $this->assign('params',$params);
            $this->assign('orderid',$params['orderid']);
            $this->assign('money',$params['amount']);
            $this->display("WeiXin/alipay");
        } else {
            echo "失败";
        }
        exit();
    }
    //同步通知
    public function callbackurl()
    {
        $Order = M("Order");
        $pay_status = $Order->where("pay_orderid = '".$_REQUEST["orderid"]."'")->getField("pay_status");
        if($pay_status <> 0){
            $this->EditMoney($_REQUEST["orderid"], 'Aliscan', 1);
        }else{
            exit("error");
        }

    }

    //异步通知
    public function notifyurl()
    {
        $response = $_POST;
        $sign = $response['sign'];
        $sign_type = $response['sign_type'];
        unset($response['sign']);
        unset($response['sign_type']);
        $publiKey =  $this->getSignkey('Aliscan', $response["app_id"]); // 密钥

        ksort($response);
        $signData = '';
        foreach ($response as $key=>$val){
            $signData .= $key .'='.$val."&";
        }
        $signData = trim($signData,'&');
        //$checkResult = $aop->verify($signData,$sign,$publiKey,$sign_type);
        $res = "-----BEGIN PUBLIC KEY-----\n" . wordwrap($publiKey, 64, "\n", true) . "\n-----END PUBLIC KEY-----";
        $result = (bool)openssl_verify($signData, base64_decode($sign), $res, OPENSSL_ALGO_SHA256);

        if($result){
            if($response['trade_status'] == 'TRADE_SUCCESS' || $response['trade_status'] == 'TRADE_FINISHED'){
                $this->EditMoney($response['out_trade_no'], 'Aliscan', 0);
                exit("success");
            }
        }else{
            exit('error:check sign Fail!');
       }

    }

    /** *利用google api生成二维码图片
     * $content：二维码内容参数
     * $size：生成二维码的尺寸，宽度和高度的值
     * $lev：可选参数，纠错等级
     * $margin：生成的二维码离边框的距离
     */
    function create_erweima($content, $size = '200', $lev = 'L', $margin= '0') {
        $content = urlencode($content);
        $image = 'http://chart.apis.google.com/chart?chs='.$size.'x'.$size.'&amp;cht=qr&chld='.$lev.'|'.$margin.'&amp;chl='.$content;
        return $image;
    }
}